mapper.update:
UPDATE ALBUM_BASIC
ALBUM_TITLE = #{albumTitle}, ALBUM_ARTIST = #{albumArtist}, ALBUM_LANGUAGE = #{albumLanguage}, ALBUM_ISSUE_DATE = #{albumIssueDate}, ALBUM_ISSUE_COMPANY = #{albumIssueCompany}, ALBUM_GENRE = #{albumGenre}, ALBUM_COVER_PHOTO = #{albumCoverPhoto}, REMARK = #{remark}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE ALBUM_CODE = #{albumCode} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ALBUM_CODE AS "albumCode",
  ALBUM_TITLE AS "albumTitle",
  ALBUM_ARTIST AS "albumArtist",
  ALBUM_LANGUAGE AS "albumLanguage",
  ALBUM_ISSUE_DATE AS "albumIssueDate",
  ALBUM_ISSUE_COMPANY AS "albumIssueCompany",
  ALBUM_GENRE AS "albumGenre",
  ALBUM_COVER_PHOTO AS "albumCoverPhoto",
  REMARK AS "remark",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM ALBUM_BASIC
WHERE 1 = 1 
  AND ALBUM_CODE = #{albumCode}
  AND ALBUM_TITLE = #{albumTitle}
  AND ALBUM_ARTIST = #{albumArtist}
  AND ALBUM_LANGUAGE = #{albumLanguage}
  AND ALBUM_ISSUE_DATE = #{albumIssueDate}
  AND ALBUM_ISSUE_COMPANY = #{albumIssueCompany}
  AND ALBUM_GENRE = #{albumGenre}
  AND ALBUM_COVER_PHOTO = #{albumCoverPhoto}
  AND REMARK = #{remark}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM ALBUM_BASIC
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND ALBUM_CODE = #{albumCode}
  AND ALBUM_TITLE = #{albumTitle}
  AND ALBUM_ARTIST = #{albumArtist}
  AND ALBUM_LANGUAGE = #{albumLanguage}
  AND ALBUM_ISSUE_DATE = #{albumIssueDate}
  AND ALBUM_ISSUE_COMPANY = #{albumIssueCompany}
  AND ALBUM_GENRE = #{albumGenre}
  AND ALBUM_COVER_PHOTO = #{albumCoverPhoto}
  AND REMARK = #{remark}
