mapper.update:
UPDATE ALBUM_TRACK
SET
  TRACK_NAME = #{trackName},
  REMARK = #{remark},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},
  UPDATE_DATETIME = current_timestamp() 
WHERE UPDATE_DATETIME = #{updateDatetime}
  AND ALBUM_CODE = #{albumCode}
  AND TRACK_NO = #{trackNo}
   AND UPDATE_DATETIME = #{updateDatetime}

UPDATE ALBUM_TRACK
TRACK_NAME = #{trackName}, REMARK = #{remark}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE ALBUM_CODE = #{albumCode} AND TRACK_NO = #{trackNo} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ALBUM_CODE AS "albumCode",
  TRACK_NO AS "trackNo",
  TRACK_NAME AS "trackName",
  REMARK AS "remark",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM ALBUM_TRACK
WHERE 1 = 1 
  AND ALBUM_CODE = #{albumCode}
  AND TRACK_NO = #{trackNo}
  AND TRACK_NAME = #{trackName}
  AND REMARK = #{remark}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM ALBUM_TRACK
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND ALBUM_CODE = #{albumCode}
  AND TRACK_NO = #{trackNo}
  AND TRACK_NAME = #{trackName}
  AND REMARK = #{remark}
